mapper.select:
select
  alert_info_key, shop_supplier_key, goods_key, status, alert_time, type
from alert_info


        and shop_supplier_key = #{shopSupplierKey}


        and goods_key = #{goodsKey}


        and status = #{status}


        and type = #{type}


limit #{startRows,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}

select count(1)
from alert_info


        and shop_supplier_key = #{shopSupplierKey}


        and goods_key = #{goodsKey}


        and status = #{status}


        and type = #{type}
