mapper.select:
select
  category_key, customer_key, customer_name, customer_code, customer_email, customer_tel, customer_address,province, city, district, detail
from customer


        and category_key = #{categoryKey,jdbcType=INTEGER}


        and customer_name like concat('%', #{customerName}, '%')


limit #{startRows,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}

select count(1)
from customer


        and category_key = #{categoryKey}


        and customer_key = #{customerKey}


        and customer_name = #{customerName}


        and customer_code = #{customerCode}


        and customer_email = #{customerEmail}


        and customer_tel = #{customerTel}


        and customer_address = #{customerAddress}


        and province = #{province}


        and city = #{city}


        and district = #{district}


        and detail = #{detail}
