mapper.select:
select
  description, district_code, inventory_districtKey, inventory_key, sort
from district


        and district_code like concat('%', #{districtCode}, '%')


        and inventory_key = #{inventoryKey}


limit #{startRows,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}

select count(1)
from district


        and description = #{description}


        and district_code = #{districtCode}


        and inventory_districtKey = #{inventoryDistrictkey}


        and inventory_key = #{inventoryKey}


        and sort = #{sort}
