mapper.select:
select
  name, icon, component_name, level, parent_menu_key, menu_key, menu_role
from menu


        and name = #{name}


        and icon = #{icon}


        and component_name = #{componentName}


        and level = #{level}


        and parent_menu_key = #{parentMenuKey}


        and menu_key = #{menuKey}


        and menu_role = #{menuRole}

select count(1)
from menu


        and name = #{name}


        and icon = #{icon}


        and component_name = #{componentName}


        and level = #{level}


        and parent_menu_key = #{parentMenuKey}


        and menu_key = #{menuKey}


        and menu_role = #{menuRole}
