mapper.select:
select
inventory_district_key, inventory_key, position_code, position_type, maxWeight, max_capacity, description,
position_key
from position


        and inventory_district_key = #{inventoryDistrictkey}


        and inventory_key = #{inventoryKey}


        and position_code like concat('%', #{positionCode}, '%')


        and position_type = #{positionType}


limit #{startRows,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}

select count(1)
from position


        and inventory_district_key = #{inventoryDistrictkey,jdbcType=VARCHAR}


        and inventory_key = #{inventoryKey,jdbcType=VARCHAR}


        and position_code = #{positionCode,jdbcType=VARCHAR}


        and position_type = #{positionType,jdbcType=VARCHAR}


        and maxWeight = #{maxWeight,jdbcType=VARCHAR}


        and columax_capacity = #{maxCapacity,jdbcType=VARCHAR}


        and description = #{description,jdbcType=VARCHAR}


        and position_key = #{positionKey,jdbcType=VARCHAR}
