mapper.select:
select supplier.supplier_key, supplier.supplier_code, supplier.supplier_name, inventory.inventory_key, inventory.inventory_code, inventory.inventory_name, inventory.description, inventory.status, inventory.belong_key, inventory.province, inventory.city, inventory.district, inventory.detail from supplier join inventory on supplier.supplier_key=inventory.belong_key
and supplier.supplier_name like concat('%', #{supplierName}, '%')
and inventory.inventory_name like concat('%', #{inventoryName}, '%')
and supplier.supplier_key =#{supplierKey}
and inventory.inventory_key =#{inventoryKey}
and inventory.status =#{status}
and inventory.inventory_type=1
limit #{startRows,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}

select count(1)
from supplier join inventory on supplier.supplier_key=inventory.belong_key


        and supplier.supplier_name like concat('%', #{supplierName}, '%')


        and inventory.inventory_name like concat('%', #{inventoryName}, '%')


        and supplier.supplier_key =#{supplierKey}


        and inventory.inventory_key =#{inventoryKey}


        and inventory.status =#{status}

    and inventory.inventory_type=1
