mapper.select:
SELECT shop_code,shop_name, count(case when(status=0) then 1 else null end)as inOrderNum, count(case when(status=1) then 1 else null end)as productNum, count(case when(status=2) then 1 else null end)as onWayNum, count(case when(status=3) then 1 else null end)as inStoreNum, count(case when(return_num) then 1 else null end)as returnCount, sum(case when(status=0) then input_plan else null end) as inOrderSum, sum(case when(status=1) then input_plan else null end) as productSum, sum(case when(status=2) then input_actual else null end) as onWaySum, sum(case when(status=3) then input_actual else null end) as inStoreSum, sum(case when(status=3) then return_num else null end) as returnSum from input_warehouse
and shop_code = #{shopCode}
and is_deleted=0
group by shop_code limit #{startRows,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}

SELECT goods_code,goods_name, count(case when(status=0) then 1 else null end)as inOrderNum, count(case when(status=1) then 1 else null end)as productNum, count(case when(status=2) then 1 else null end)as onWayNum, count(case when(status=3) then 1 else null end)as inStoreNum, count(case when(return_num) then 1 else null end)as returnCount, sum(case when(status=0) then input_plan else null end) as inOrderSum, sum(case when(status=1) then input_plan else null end) as productSum, sum(case when(status=2) then input_actual else null end) as onWaySum, sum(case when(status=3) then input_actual else null end) as inStoreSum, sum(case when(status=3) then return_num else null end) as returnSum from input_warehouse
and goods_code = #{goodsCode}
and is_deleted=0
group by goods_code limit #{startRows,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}
